		private LoginResponse ProcessLoginResponse(HttpDataResponse<LoginResponse> data) {
			var login = false;
			if (data.IsDataGood) {
				if (data.Data.Login) {
					User = data.Data.User;
					IsLoggedIn = true;
					login = true;
					StartWebSocked();
				}
			}
			if (!login) {
				IsLoggedIn = false;
				User = null;
				_client?.CloseAsync(WebSocketCloseStatus.NormalClosure, "", CancellationToken.None).Wait();
				_client?.Dispose();
				_client = null;
			}
			return data.Data;
		}

